const soundEffect = new Audio();

let empty = "data:audio/mpeg;base64,";
let music = "pitbull.mp3";

soundEffect.src = empty;

soundEffect.autoplay = true;

// let audio = new Audio("pitbull.mp3");

function play() {
    //soundEffect.currentTime = 134;
    soundEffect.src = music;
}

function pause() {
    soundEffect.src = empty;
}

document.addEventListener("click", function(event) {
    let element = document.getElementById("card"); // Replace with your element's ID
    if (!element.contains(event.target)) {
        pause();
    }
});